import { Link } from "react-router-dom";
import { useEffect, useState } from "react";

const Navbar = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem("token");
    setIsLoggedIn(!!token);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    setIsLoggedIn(false);
    window.location.href = "/login";
  };

  return (
    <nav className="bg-white shadow-lg">
      <div className="max-w-6xl mx-auto px-4">
        <div className="flex justify-between">
          <div className="flex space-x-7">
            <div>
              <Link to="/" className="flex items-center py-4 px-2">
                <span className="font-semibold text-gray-500 text-2xl">
                  FOOD GALLERY
                </span>
              </Link>
            </div>
            <div className="hidden md:flex items-center space-x-1">
              <Link
                to="/posts"
                className="py-4 px-2 text-gray-500 font-semibold hover:text-primary transition duration-300"
              >
                Food Posts
              </Link>
              <Link
                to="/recipes"
                className="py-4 px-2 text-gray-500 font-semibold hover:text-primary transition duration-300"
              >
                Food Recipes
              </Link>
              <Link
                to="/decorations"
                className="py-4 px-2 text-gray-500 font-semibold hover:text-primary transition duration-300"
              >
                Decorations
              </Link>
            </div>
          </div>
          <div className="hidden md:flex items-center space-x-3">
            {isLoggedIn ? (
              <button
                onClick={handleLogout}
                className="py-2 px-2 font-medium text-white bg-primary rounded hover:bg-primary-dark transition duration-300"
              >
                Logout
              </button>
            ) : (
              <>
                <Link
                  to="/login"
                  className="py-2 px-2 font-medium text-gray-500 rounded hover:bg-primary hover:text-white transition duration-300"
                >
                  Sign In
                </Link>
                <Link
                  to="/register"
                  className="py-2 px-2 font-medium text-white bg-primary rounded hover:bg-primary-dark transition duration-300"
                >
                  Sign Up
                </Link>
              </>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
